const fs = require('fs');
const path = require('path');

// Extends is a keyword, so use tencentExtends as the varible name.
const tencentExtends = ['./base.js', './import.js'];

// Check typescript exist and append the rules.
const typescriptPathes = [
  path.resolve(__dirname, '../../typescript'),
  path.resolve(__dirname, './node_modules/typescript'),
];
if (typescriptPathes.some(path => fs.existsSync(path))) {
  console.warn('[eslint-config-tencent] Applied typescript rules');
  tencentExtends.push('./ts.js');
}

module.exports = {
  extends: tencentExtends,
};
